package org.moparscape.msc.gs.external;

/**
 * The definition wrapper for ores
 */
public class ReqOreDef {

    /**
     * The amount of the ore required
     */
    public int amount;
    /**
     * The id of the ore
     */
    public int oreId;

    public int getAmount() {
	return amount;
    }

    public int getId() {
	return oreId;
    }

}
